definition.script:
node () {

	stage ('Git Checkout') {
 	 checkout([$class: 'GitSCM', branches: [[name: '*/master']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: '', url: 'https://github.com/TechnologyBootCamps/functional-assurance']]])
	}
	stage ('Run Selenium Grid') {
	    if (isUnix()) {
            sh """
                 docker rm -f \$(docker ps -a -q)
                 docker run -d -p 4444:4444 --name selenium-hub selenium/hub
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-firefox
             """ 
	    }
	    else
	    {
	        bat """
                 docker rm -f \$(docker ps -a -q)
                 docker run -d -p 4444:4444 --name selenium-hub selenium/hub
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-firefox
             """  
	    }
	} 

	stage ('Inrease Chrome instances') {
	    if (isUnix()) {
            sh """
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome

             """ 
	    }
	    else
	    {
	        bat """
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome
                 docker run -d --link selenium-hub:hub selenium/node-chrome

             """ 
	    }
	} 

	stage ('Execute Scripts') {
	    if (isUnix()) {
            sh """
            mvn clean test -Dcucumber.options="--tags '@UITest and @Smoke'" -DexecutionPlatform="GRID_FIREFOX"
            """
	    }
	    else
	    {
	      bat """
            mvn clean test -Dcucumber.options="--tags '@UITest and @Smoke'" -DexecutionPlatform="GRID_FIREFOX"
          """  
	    }
	}
}
