definition.script:
node () {
    stage ('Git Checkout') {
 	 checkout([$class: 'GitSCM', branches: [[name: '*/selenium-test-compose']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: '', url: 'https://github.com/TechnologyBootCamps/functional-assurance']]])
	}
	stage ('Exeucte Selenium-tests Image') {
	    dir("dockersample\\selenium-tests-with-zalenium-compose") {
	        if (isUnix()) {
                sh """
                docker rm -f \$(docker ps -a -q)
                docker-compose run --rm selenium-tests
                """ 
	        }
	        else
             {
               bat """
                docker rm -f \$(docker ps -a -q)
                docker-compose run --rm selenium-tests
                """   
             }
	    }
	}
}
