definition.script:
timestamps {
    node () {
    	stage ('Git Checkout') {
     	 checkout([$class: 'GitSCM', branches: [[name: '*/master']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: '', url: 'https://github.com/TechnologyBootCamps/functional-assurance']]])
    	}
    	stage ('Execute Scripts') {
            sh """
            mvn clean test -Dcucumber.options="--tags '@UITest and @Smoke'" -DexecutionPlatform="LOCAL_CHROME"
             """ 
    	}
    	stage ('Publish Results') {
    	    publishHTML([allowMissing: false, alwaysLinkToLastBuild: false, keepAll: false, reportDir: 'target', reportFiles: 'extent-reports.html', reportName: 'Extent Report', reportTitles: ''])
    	}
    }
}
